import React from "react";

const NewsCard = ({ article, openArticle }) => {
  const { urlToImage, title, description, publishedAt, source, url } = article;

  const date = new Date(publishedAt).toLocaleString("en-US", {
    timeZone: "Asia/Jakarta",
  });

  return (
    <div className="card" onClick={() => openArticle(url)}>
      <div className="card-header">
        <img src={urlToImage} alt="news-image" id="news-img" />
      </div>
      <div className="card-content">
        <h3 id="news-title">{title}</h3>
        <h6 className="news-source" id="news-source">
          {source.name} Â· {date}
        </h6>
        <p className="news-desc" id="news-desc">
          {description}
        </p>
      </div>
    </div>
  );
};

export default NewsCard;
